var lazy= `@lazyRule=.js:var src = input;if(src.indexOf('html')!=-1){var jx='http://parse.tyun77.cn/973jiexi/api/api.php?pltfrom=1100&url='+src;var play=JSON.parse(fetch(jx,{})).url;if(play.indexOf('jiexi')!=-1){JSON.parse(fetch(play, {headers:{"User-Agent":"Lavf/57.83.100"}, redirect:false, withHeaders:true})).headers.location[0];}else{play};}else{if(src.indexOf('GetDownUrl')!=-1){src+'；；{User-Agent@Lavf/57.83.100}';}else{src}}`;
