eval('json=' + getResCode());
var res = {};
var d = [];
for (var i = 0; i < json.data.episodes.length; i++) {
    var r = {};
    var j = json.data.episodes[i];
    r.title = j.episode+'';
    r.url = j.playurl+lazy;
    r.col_type = mult== '1' ? 'text_center_1' : 'text_2';
    d.push(r);
if(mult=='1'){
    for (var i2 = 0; i2< j.playurls.length; i2++) {
        var r2 = {};
